import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { AppDispatch } from '../../store';
import DataTable from '../../components/DataTable';
import Form from '../../components/Form';
import api from '../../services/api';

interface Payment {
  id: number;
  student_id: number;
  student_name: string;
  fee_id: number;
  fee_name: string;
  amount: number;
  payment_date: string;
  payment_method: 'cash' | 'card' | 'bank_transfer';
  transaction_id: string;
  status: 'pending' | 'completed' | 'failed';
  notes: string;
}

interface Student {
  id: number;
  name: string;
}

interface Fee {
  id: number;
  name: string;
  amount: number;
}

const Payments: React.FC = () => {
  const dispatch = useDispatch<AppDispatch>();
  const [payments, setPayments] = useState<Payment[]>([]);
  const [students, setStudents] = useState<Student[]>([]);
  const [fees, setFees] = useState<Fee[]>([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [selectedPayment, setSelectedPayment] = useState<Payment | null>(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const columns = [
    { header: 'Student', accessor: 'student_name' },
    { header: 'Fee Type', accessor: 'fee_name' },
    { header: 'Amount', accessor: 'amount', format: (value: number) => `$${value.toFixed(2)}` },
    { header: 'Payment Date', accessor: 'payment_date' },
    { header: 'Payment Method', accessor: 'payment_method' },
    { header: 'Status', accessor: 'status' },
    { header: 'Transaction ID', accessor: 'transaction_id' },
  ];

  const formFields = [
    {
      name: 'student_id',
      label: 'Student',
      type: 'select',
      options: students.map(student => ({
        value: student.id,
        label: student.name,
      })),
      required: true,
    },
    {
      name: 'fee_id',
      label: 'Fee',
      type: 'select',
      options: fees.map(fee => ({
        value: fee.id,
        label: `${fee.name} ($${fee.amount})`,
      })),
      required: true,
    },
    { name: 'amount', label: 'Amount', type: 'number', required: true, min: 0, step: '0.01' },
    { name: 'payment_date', label: 'Payment Date', type: 'date', required: true },
    {
      name: 'payment_method',
      label: 'Payment Method',
      type: 'select',
      options: [
        { value: 'cash', label: 'Cash' },
        { value: 'card', label: 'Card' },
        { value: 'bank_transfer', label: 'Bank Transfer' },
      ],
      required: true,
    },
    { name: 'transaction_id', label: 'Transaction ID', type: 'text' },
    {
      name: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'pending', label: 'Pending' },
        { value: 'completed', label: 'Completed' },
        { value: 'failed', label: 'Failed' },
      ],
      required: true,
    },
    { name: 'notes', label: 'Notes', type: 'textarea' },
  ];

  const fetchPayments = async (page: number = 1) => {
    try {
      setLoading(true);
      const response = await api.get(`/payments?page=${page}&limit=10`);
      setPayments(response.data.items);
      setTotalPages(Math.ceil(response.data.total / 10));
      setCurrentPage(page);
    } catch (error) {
      console.error('Error fetching payments:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchStudentsAndFees = async () => {
    try {
      const [studentsResponse, feesResponse] = await Promise.all([
        api.get('/students'),
        api.get('/fees'),
      ]);
      setStudents(studentsResponse.data.items);
      setFees(feesResponse.data.items);
    } catch (error) {
      console.error('Error fetching students and fees:', error);
    }
  };

  useEffect(() => {
    fetchPayments();
    fetchStudentsAndFees();
  }, []);

  const handleSubmit = async (data: Partial<Payment>) => {
    try {
      if (selectedPayment) {
        await api.put(`/payments/${selectedPayment.id}`, data);
      } else {
        await api.post('/payments', data);
      }
      setShowForm(false);
      setSelectedPayment(null);
      fetchPayments(currentPage);
    } catch (error) {
      console.error('Error saving payment:', error);
    }
  };

  const handleEdit = (payment: Payment) => {
    setSelectedPayment(payment);
    setShowForm(true);
  };

  const handleDelete = async (payment: Payment) => {
    if (window.confirm('Are you sure you want to delete this payment?')) {
      try {
        await api.delete(`/payments/${payment.id}`);
        fetchPayments(currentPage);
      } catch (error) {
        console.error('Error deleting payment:', error);
      }
    }
  };

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-semibold text-gray-900">Payments</h1>
        <button
          className="btn-primary"
          onClick={() => {
            setSelectedPayment(null);
            setShowForm(true);
          }}
        >
          Record New Payment
        </button>
      </div>

      <DataTable
        data={payments}
        columns={columns}
        loading={loading}
        currentPage={currentPage}
        totalPages={totalPages}
        onPageChange={fetchPayments}
        onEdit={handleEdit}
        onDelete={handleDelete}
      />

      {showForm && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center">
          <div className="bg-white rounded-lg p-6 w-full max-w-2xl">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-semibold">
                {selectedPayment ? 'Edit Payment' : 'Record New Payment'}
              </h2>
              <button
                className="text-gray-500 hover:text-gray-700"
                onClick={() => {
                  setShowForm(false);
                  setSelectedPayment(null);
                }}
              >
                Ã—
              </button>
            </div>

            <Form
              fields={formFields}
              initialValues={selectedPayment || {}}
              onSubmit={handleSubmit}
              onCancel={() => {
                setShowForm(false);
                setSelectedPayment(null);
              }}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default Payments;
