import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { AppDispatch } from '../../store';
import DataTable from '../../components/DataTable';
import Form from '../../components/Form';
import api from '../../services/api';

interface Student {
  id: number;
  name: string;
  email: string;
  phone: string;
  address: string;
  enrollment_date: string;
  status: 'active' | 'inactive';
}

const Students: React.FC = () => {
  const dispatch = useDispatch<AppDispatch>();
  const [students, setStudents] = useState<Student[]>([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [selectedStudent, setSelectedStudent] = useState<Student | null>(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const columns = [
    { header: 'Name', accessor: 'name' },
    { header: 'Email', accessor: 'email' },
    { header: 'Phone', accessor: 'phone' },
    { header: 'Status', accessor: 'status' },
    { header: 'Enrollment Date', accessor: 'enrollment_date' },
  ];

  const formFields = [
    { name: 'name', label: 'Name', type: 'text', required: true },
    { name: 'email', label: 'Email', type: 'email', required: true },
    { name: 'phone', label: 'Phone', type: 'tel', required: true },
    { name: 'address', label: 'Address', type: 'text', required: true },
    { name: 'enrollment_date', label: 'Enrollment Date', type: 'date', required: true },
    {
      name: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'active', label: 'Active' },
        { value: 'inactive', label: 'Inactive' },
      ],
      required: true,
    },
  ];

  const fetchStudents = async (page: number = 1) => {
    try {
      setLoading(true);
      const response = await api.get(`/students?page=${page}&limit=10`);
      setStudents(response.data.items);
      setTotalPages(Math.ceil(response.data.total / 10));
      setCurrentPage(page);
    } catch (error) {
      console.error('Error fetching students:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  const handleSubmit = async (data: Partial<Student>) => {
    try {
      if (selectedStudent) {
        await api.put(`/students/${selectedStudent.id}`, data);
      } else {
        await api.post('/students', data);
      }
      setShowForm(false);
      setSelectedStudent(null);
      fetchStudents(currentPage);
    } catch (error) {
      console.error('Error saving student:', error);
    }
  };

  const handleEdit = (student: Student) => {
    setSelectedStudent(student);
    setShowForm(true);
  };

  const handleDelete = async (student: Student) => {
    if (window.confirm('Are you sure you want to delete this student?')) {
      try {
        await api.delete(`/students/${student.id}`);
        fetchStudents(currentPage);
      } catch (error) {
        console.error('Error deleting student:', error);
      }
    }
  };

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-semibold text-gray-900">Students</h1>
        <button
          className="btn-primary"
          onClick={() => {
            setSelectedStudent(null);
            setShowForm(true);
          }}
        >
          Add New Student
        </button>
      </div>

      <DataTable
        data={students}
        columns={columns}
        loading={loading}
        currentPage={currentPage}
        totalPages={totalPages}
        onPageChange={fetchStudents}
        onEdit={handleEdit}
        onDelete={handleDelete}
      />

      {showForm && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center">
          <div className="bg-white rounded-lg p-6 w-full max-w-2xl">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-semibold">
                {selectedStudent ? 'Edit Student' : 'Add New Student'}
              </h2>
              <button
                className="text-gray-500 hover:text-gray-700"
                onClick={() => {
                  setShowForm(false);
                  setSelectedStudent(null);
                }}
              >
                Ã—
              </button>
            </div>

            <Form
              fields={formFields}
              initialValues={selectedStudent || {}}
              onSubmit={handleSubmit}
              onCancel={() => {
                setShowForm(false);
                setSelectedStudent(null);
              }}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default Students;
