import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { AppDispatch } from '../../store';
import DataTable from '../../components/DataTable';
import Form from '../../components/Form';
import api from '../../services/api';

interface Invoice {
  id: number;
  student_id: number;
  student_name: string;
  invoice_number: string;
  issue_date: string;
  due_date: string;
  total_amount: number;
  paid_amount: number;
  status: 'draft' | 'issued' | 'paid' | 'overdue' | 'cancelled';
  items: InvoiceItem[];
  notes: string;
}

interface InvoiceItem {
  fee_id: number;
  fee_name: string;
  amount: number;
  description: string;
}

interface Student {
  id: number;
  name: string;
}

interface Fee {
  id: number;
  name: string;
  amount: number;
}

const Invoices: React.FC = () => {
  const dispatch = useDispatch<AppDispatch>();
  const [invoices, setInvoices] = useState<Invoice[]>([]);
  const [students, setStudents] = useState<Student[]>([]);
  const [fees, setFees] = useState<Fee[]>([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [selectedInvoice, setSelectedInvoice] = useState<Invoice | null>(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const columns = [
    { header: 'Invoice Number', accessor: 'invoice_number' },
    { header: 'Student', accessor: 'student_name' },
    { header: 'Issue Date', accessor: 'issue_date' },
    { header: 'Due Date', accessor: 'due_date' },
    { header: 'Total Amount', accessor: 'total_amount', format: (value: number) => `$${value.toFixed(2)}` },
    { header: 'Paid Amount', accessor: 'paid_amount', format: (value: number) => `$${value.toFixed(2)}` },
    { header: 'Status', accessor: 'status' },
  ];

  const formFields = [
    {
      name: 'student_id',
      label: 'Student',
      type: 'select',
      options: students.map(student => ({
        value: student.id,
        label: student.name,
      })),
      required: true,
    },
    { name: 'issue_date', label: 'Issue Date', type: 'date', required: true },
    { name: 'due_date', label: 'Due Date', type: 'date', required: true },
    {
      name: 'status',
      label: 'Status',
      type: 'select',
      options: [
        { value: 'draft', label: 'Draft' },
        { value: 'issued', label: 'Issued' },
        { value: 'paid', label: 'Paid' },
        { value: 'overdue', label: 'Overdue' },
        { value: 'cancelled', label: 'Cancelled' },
      ],
      required: true,
    },
    { name: 'notes', label: 'Notes', type: 'textarea' },
  ];

  const fetchInvoices = async (page: number = 1) => {
    try {
      setLoading(true);
      const response = await api.get(`/invoices?page=${page}&limit=10`);
      setInvoices(response.data.items);
      setTotalPages(Math.ceil(response.data.total / 10));
      setCurrentPage(page);
    } catch (error) {
      console.error('Error fetching invoices:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchStudentsAndFees = async () => {
    try {
      const [studentsResponse, feesResponse] = await Promise.all([
        api.get('/students'),
        api.get('/fees'),
      ]);
      setStudents(studentsResponse.data.items);
      setFees(feesResponse.data.items);
    } catch (error) {
      console.error('Error fetching students and fees:', error);
    }
  };

  useEffect(() => {
    fetchInvoices();
    fetchStudentsAndFees();
  }, []);

  const handleSubmit = async (data: Partial<Invoice>) => {
    try {
      if (selectedInvoice) {
        await api.put(`/invoices/${selectedInvoice.id}`, data);
      } else {
        await api.post('/invoices', data);
      }
      setShowForm(false);
      setSelectedInvoice(null);
      fetchInvoices(currentPage);
    } catch (error) {
      console.error('Error saving invoice:', error);
    }
  };

  const handleEdit = (invoice: Invoice) => {
    setSelectedInvoice(invoice);
    setShowForm(true);
  };

  const handleDelete = async (invoice: Invoice) => {
    if (window.confirm('Are you sure you want to delete this invoice?')) {
      try {
        await api.delete(`/invoices/${invoice.id}`);
        fetchInvoices(currentPage);
      } catch (error) {
        console.error('Error deleting invoice:', error);
      }
    }
  };

  const handleGeneratePDF = async (invoice: Invoice) => {
    try {
      const response = await api.get(`/invoices/${invoice.id}/pdf`, {
        responseType: 'blob',
      });

      const blob = new Blob([response.data], { type: 'application/pdf' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `invoice-${invoice.invoice_number}.pdf`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Error generating PDF:', error);
    }
  };

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-semibold text-gray-900">Invoices</h1>
        <button
          className="btn-primary"
          onClick={() => {
            setSelectedInvoice(null);
            setShowForm(true);
          }}
        >
          Create New Invoice
        </button>
      </div>

      <DataTable
        data={invoices}
        columns={columns}
        loading={loading}
        currentPage={currentPage}
        totalPages={totalPages}
        onPageChange={fetchInvoices}
        onEdit={handleEdit}
        onDelete={handleDelete}
        actions={[
          {
            label: 'Generate PDF',
            onClick: handleGeneratePDF,
            className: 'btn-secondary',
          },
        ]}
      />

      {showForm && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center">
          <div className="bg-white rounded-lg p-6 w-full max-w-2xl">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-semibold">
                {selectedInvoice ? 'Edit Invoice' : 'Create New Invoice'}
              </h2>
              <button
                className="text-gray-500 hover:text-gray-700"
                onClick={() => {
                  setShowForm(false);
                  setSelectedInvoice(null);
                }}
              >
                Ã—
              </button>
            </div>

            <Form
              fields={formFields}
              initialValues={selectedInvoice || {}}
              onSubmit={handleSubmit}
              onCancel={() => {
                setShowForm(false);
                setSelectedInvoice(null);
              }}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default Invoices;
